mapper.select:
    SELECT c.*,d.dictionary_name repaymentType1
from(
SELECT b.*,d.dictionary_name repaymentId1
from (
SELECT a.Apply_Id ApplyId,a.User_id UserId,a.Loan_type loanType,a.Loan_money loanMoney,
a.loan_deadline loanDeadline,a.Repayment_type,a.Repayment_id,a.bondsman_name bondsmanName,
a.bondsman_age bondsmanAge,a.bondsman_sex bondsmanSex,a.bondsman_tel bondsmanTel,a.bondsman_zj bondsmanZj,
a.bondsman_sfzz bondsmanSfzz,a.bondsman_sfzf bondsmanSfzf,a.bondsman_add bondsmanAdd,
a.income,a.describes,a.Loan_time applyTime,a.loan_status loanStatus,d.dictionary_name loanType1
from apply a join dictionary d on d.dictionary_value=a.Loan_type where keyword='loan_type'
) b join dictionary d on d.dictionary_value=b.Repayment_id where keyword='Repayment'
) c join dictionary d on d.dictionary_value=c.repayment_type where keyword='repayment_type' and
   ApplyId=#{applyId}

    SELECT e.*,d.dictionary_name loanStatus1
from (
SELECT c.*,d.dictionary_name repaymentType1
from(
SELECT b.*,d.dictionary_name repaymentId1
from (
SELECT a.Apply_Id ApplyId,a.User_id UserId,a.Loan_type loanType,a.Loan_money loanMoney,
a.loan_deadline loanDeadline,a.Repayment_type,a.Repayment_id,a.bondsman_name bondsmanName,
a.bondsman_age bondsmanAge,a.bondsman_sex bondsmanSex,a.bondsman_tel bondsmanTel,a.bondsman_zj bondsmanZj,
a.bondsman_sfzz bondsmanSfzz,a.bondsman_sfzf bondsmanSfzf,a.bondsman_add bondsmanAdd,
a.income,a.describes,date_format(a.Loan_time,'%Y-%m-%d %H:%i:%s') applyTime,a.loan_status loanStatus,d.dictionary_name loanType1
from apply a join dictionary d on d.dictionary_value=a.Loan_type where keyword='loan_type'
) b join dictionary d on d.dictionary_value=b.Repayment_id where keyword='Repayment'
) c join dictionary d on d.dictionary_value=c.repayment_type where keyword='repayment_type'
) e JOIN dictionary d on d.dictionary_value=e.loanStatus where keyword='loan_status' and UserId=#{userId}

select Apply_Id applyId,d.dictionary_name loanStatus,d1.dictionary_name loanType ,d2.dictionary_name repaymentType,d3.dictionary_name repaymentId, cp.cp_name cpName,a.loan_money loanMoney,a.bondsman_name bondsmanName,law.law_person lawName,date_format(a.loan_time,'%Y-%m-%d %H:%i:%s') loanTime from apply a , dictionary d ,dictionary d1 ,dictionary d2 , dictionary d3,cp_info cp,login,law_person law WHERE ( a.loan_status=d.dictionary_value and d.keyword='loan_status') and (a.Loan_type=d1.dictionary_value and d1.keyword='loan_type') and (a.Repayment_type=d2.dictionary_value and d2.keyword='repayment_type') and (a.Repayment_id=d3.dictionary_value and d3.keyword='repayment') and (a.user_id=login.id and cp.username=login.username) and (cp.username=law.username)and a.loan_status=#{loanStatus} -- 根据公司名称查询
and cp.cp_name like concat('%',#{cpName},'%')
-- 贷款类型 房贷 抵押
and a.loan_type=#{loanType}
-- 还款类型 等额本金 等额本息
and a.Repayment_type=#{rType}
and a.Repayment_id=#{RepaymentId}
limit ${start},${pageSize}

select count(*) from apply a , dictionary d ,dictionary d1 ,dictionary d2 , dictionary d3,cp_info cp,login,law_person law WHERE ( a.loan_status=d.dictionary_value and d.keyword='loan_status') and (a.Loan_type=d1.dictionary_value and d1.keyword='loan_type') and (a.Repayment_type=d2.dictionary_value and d2.keyword='repayment_type') and (a.Repayment_id=d3.dictionary_value and d3.keyword='repayment') and (a.user_id=login.id and cp.username=login.username) and (cp.username=law.username)and a.loan_status=#{loanStatus} -- 根据公司名称查询
and cp.cp_name like concat('%',#{cpName},'%')
-- 贷款类型 房贷 抵押
and a.loan_type=#{loanType}
-- 还款类型 等额本金 等额本息
and a.Repayment_type=#{rType}
and a.Repayment_id=#{RepaymentId}
